import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import io from 'socket.io-client'
import { SocketEvent, Party, PlaybackState } from '../common';
import axios, { AxiosResponse } from 'axios';
import Button from '../components/Button';
import { getHashParams } from '../helpers/helpers';
import { withRouter, match, useHistory } from 'react-router';

import { useUser } from '../contexts/UserContext';
import socket from '../socket';
import { User } from '../common';
import { SPOTIFY_API } from '../const';

interface Props {
    match: any;
}

const PartyPage: React.FC<Props> = ({ match }): React.ReactElement => {
    const { user, isLoading } = useUser();
    const [currentParty, setCurrentParty] = useState<Party | undefined>(undefined);
    const partyId = match.params.id;
    const history = useHistory();
    let pollCurrentlyPlaying: any;

    useEffect(() => {
        console.log('loaded party first time')

        socket.on(SocketEvent.USER_LEFT_PARTY_RES, (party: Party) => {
            console.log('USER LEFT PARTY', party);
            setCurrentParty(party);
        });

        socket.on(SocketEvent.PARTY_PLAYBACK_CHANGED_RES, (newPartyState: Party) => {
            console.log('Playback state changed.');
            const isAdminUser = newPartyState.adminUser?.id === user?.id;
            if (!isAdminUser) {
                if (newPartyState.playbackState) {
                    setCurrentlyPlaying(newPartyState.playbackState);
                }
            }

            setCurrentParty(newPartyState);
        });

        socket.on(SocketEvent.PARTY_NOT_FOUND_RES, (party: Party) => {
            console.log('This party was not found... redirecting back');
            setTimeout(() => {
                history.push('/');
            }, 2000);
        });

        socket.on(SocketEvent.PARTY_NEW_USER_JOINED_RES, (party: Party) => {
            console.log('New user joined,', party.users ? party.users[party.users?.length - 1] : '');
            setCurrentParty(party);
        })

        socket.on(SocketEvent.PARTY_JOINED_RES, (party: Party) => {
            if (party.adminUser?.id === user?.id) {
                pollCurrentlyPlaying = setInterval(() => {
                    getCurrentlyPlaying(party.id);
                }, 5000);
            }
            setCurrentParty(party);
            if (party.playbackState && party.playbackState.is_playing) {
                setCurrentlyPlaying(party.playbackState);
            }
        });


        // NEED TO CHECK IF PARTY EXISTS HERE 
        return () => handleUserLeaving();
    }, [])

    const getCurrentlyPlaying = (partyId: string) => {
        console.log('Getting currently playing...');
        axios.get(SPOTIFY_API.CURRENTLY_PLAYING)
            .then((res: AxiosResponse<PlaybackState>) => {
                socket.emit(SocketEvent.PARTY_PLAYBACK_REQ, { playbackState: res.data, partyId: partyId })
            })
            .catch(err => { console.log('could not get currently playing') })
    }

    const setCurrentlyPlaying = (playbackState: PlaybackState) => {
        console.log('setting currently playing...', playbackState);
        const args = {
            uris: [playbackState.item.uri]
        }
        axios.put(SPOTIFY_API.PLAY, args)
            .then(() => console.log('Playing new song: ', playbackState.item.name))
            .catch(err => console.log('could not set new playback state. ', err));
    }

    const handleUserLeaving = () => {
        clearInterval(pollCurrentlyPlaying);
        socket.emit(SocketEvent.USER_LEFT_PARTY_REQ)
    }


    useEffect(() => {
        if (!user || isLoading) {
            console.log('You are not authed');
            return;
        }

        console.log('Logged in as ', user.id);

        socket.emit(SocketEvent.PARTY_JOINED_REQ, { user: user, socketId: socket.id, partyId: partyId })
    }, [user])

    // const onPartyJoinedUnauthed = (numberOfUsers: number) => {
    //     console.log('PARTY JOINED BACK')
    //     setNumberOfUsers(numberOfUsers);
    // }

    return (
        <div>
            {user && user.id ?
                <div>
                    <h1>A duper dope party</h1>
                    <h2>Hi, {user.display_name}!</h2>
                    <div>The DJ is {currentParty?.adminUser?.display_name}</div>
                    <div>Users in party: {currentParty?.users?.map(user => user.display_name).join(', ')}</div>
                    {currentParty?.playbackState ?
                        <div>
                            <div className="song">Currently playing</div>
                            <h3>{currentParty?.playbackState?.item.name}</h3>
                            <h3>{currentParty?.playbackState?.item.album.artists[0].name}</h3>
                        </div>
                        :
                        <div>No tracks playing</div>
                    }
                </div>
                :
                <div>This is a party, but you aint authed.</div>}
        </div>);

};

export default PartyPage;
