import React, { useState, useEffect, useCallback, useMemo, createContext, useContext } from "react";

import axios, { AxiosResponse } from 'axios';
import { User, SocketEvent } from '../common';

import { getHashParams } from '../helpers/helpers';
import { SPOTIFY_API } from "../const";
import socket from "../socket";

interface AuthHashParams {
    access_token: string;
    token_type: string;
}


export interface UserContext {
    user: User | undefined;
    isLoading: boolean;
    logout: () => void;
}

const UserContext = createContext<UserContext>({
    user: undefined,
    isLoading: false,
    logout: () => { },
});

const UserProvider: React.FC = ({ children }): React.ReactElement => {
    const [user, setUser] = useState<User>();
    const [isLoading, setIsLoading] = useState(true);

    const updateUser = (user: any) => {
        setUser(user);
        socket.emit(SocketEvent.USER_CONNECTED_REQ, user);
    }

    useEffect(() => {
        console.log('checklogin')
        const savedUser = localStorage.getItem('user');
        if (savedUser) {
            updateUser(JSON.parse(savedUser));
            setIsLoading(false);
            return;
        }

        if (!window.location.hash) return;
        login();
    }, [])

    const login = (): void => {
        let locationArgs: AuthHashParams = getHashParams(window.location.hash);
        axios.defaults.headers.common['Authorization'] = 'Bearer ' + locationArgs.access_token;
        axios.get(SPOTIFY_API.ME).then((res: AxiosResponse<User>) => {
            const user = res.data;
            updateUser(user)
            localStorage.setItem('user', JSON.stringify(user));
        });
    }

    const logout = (): void => {
        setUser(undefined);
        socket.emit(SocketEvent.USER_DISCONNECTED_REQ);
        localStorage.clear();
    }

    const SettingsContext = useMemo(() => ({
        user,
        isLoading,
        logout
    }), [user, logout]);

    return (
        <UserContext.Provider value={SettingsContext}>
            {children}
        </UserContext.Provider>
    );
};

const useUser = () => useContext(UserContext);
export { UserProvider, useUser };
