import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import io from 'socket.io-client'

import axios from 'axios';
import Button from '../components/Button';
import { getHashParams } from '../helpers/helpers';
import { useUser } from '../contexts/UserContext';
import { Party } from '../../../models/User';


const Home = (): React.ReactElement => {
    const APP_URL = 'http://localhost:8080'
    const LOGIN_URL = APP_URL + '/login';

    const history = useHistory();

    const { user, logout } = useUser();

    console.log(user);

    const socket = io(APP_URL);
    socket.on('party-created', (party: Party) => {
        console.log(party);
        history.push('/party', { id: party.id })
    });

    const createRoom = (): void => {
        socket.emit('party-create', socket.id);
    }

    return (<div>
        {user && user.id ?
            <div>
                <div>Hi, {user.id}!</div>
                <Button name="Logout" onClick={logout}></Button>
                <Button name="Create new Room" onClick={createRoom}></Button>
            </div>
            :
            <a href={LOGIN_URL}>Login</a>}
    </div>);

};

export default Home;
