import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import io from 'socket.io-client'
import { EventType, Party } from '../common';
import axios from 'axios';
import Button from '../components/Button';
import { getHashParams } from '../helpers/helpers';
import { withRouter, match } from 'react-router';

import { useUser } from '../contexts/UserContext';
import socket from '../socket';
import { User } from '../common';

interface Props {
    match: any;
}

const PartyPage: React.FC<Props> = ({ match }): React.ReactElement => {
    const { user } = useUser();
    const partyId = match.params.id;

    const [numberOfUsers, setNumberOfUsers] = useState(0);

    console.log(partyId);


    const [usersInParty, setUsersInParty] = useState<User[]>([]);

    useEffect(() => {
        const joinedPartyArgs = { socketId: socket.id, partyId: partyId };
        const eventType = user ? EventType.PARTY_JOINED_REQ : EventType.PARTY_JOINED_UNAUTHED_REQ;
        socket.emit(eventType, joinedPartyArgs)

        // init listeners
        socket.on(EventType.PARTY_JOINED_RES, onPartyJoined);
        socket.on(EventType.PARTY_JOINED_UNAUTHED_RES, onPartyJoinedUnauthed);
    }, [])

    const onPartyJoined = (party: Party) => {
        console.log('party state', party);
    }

    const onPartyJoinedUnauthed = (numberOfUsers: number) => {
        setNumberOfUsers(numberOfUsers);
    }

    return (
        <div>
            {user && user.id ?
                <div>
                    <div>Hi, {user.id}!</div>
                </div>
                :
                <div>This is a party of {numberOfUsers}, but you aint authed.</div>}
            <div>{usersInParty.map(user => user.display_name)}</div>
        </div>);

};

export default PartyPage;
