import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import io from 'socket.io-client'
import { SocketEvent, Party, PlaybackState } from '../common';
import axios, { AxiosResponse } from 'axios';
import Button from '../components/Button';
import { getHashParams } from '../helpers/helpers';
import { withRouter, match, useHistory } from 'react-router';

import { useUser } from '../contexts/UserContext';
import socket from '../socket';
import { User } from '../common';
import { SPOTIFY_API } from '../const';

interface Props {
    match: any;
}

const PartyPage: React.FC<Props> = ({ match }): React.ReactElement => {
    const { user, isLoading } = useUser();
    const [party, setParty] = useState<Party | undefined>(undefined);
    const partyId = match.params.id;
    const history = useHistory();
    let pollCurrentlyPlaying: any;

    useEffect(() => {
        console.log('loaded party first time')

        socket.on(SocketEvent.USER_LEFT_PARTY_RES, (party: Party) => {
            console.log('USER LEFT PARTY', party);
            setParty(party);
        });

        socket.on(SocketEvent.PARTY_PLAYBACK_CHANGED_RES, (newPartyState: Party) => {
            console.log('NEW PLAYBACK STATE', newPartyState.playbackState);
            setParty(newPartyState);
            // also check if playbackstate changed
            if (newPartyState.adminUser?.id !== user?.id && newPartyState.playbackState) {
                setCurrentlyPlaying(newPartyState.playbackState);
            }
        });

        socket.on(SocketEvent.PARTY_NOT_FOUND_RES, (party: Party) => {
            console.log('This party was not found... redirecting back');
            setTimeout(() => {
                history.push('/');
            }, 2000);
        });

        socket.on(SocketEvent.PARTY_JOINED_RES, (party: Party) => {
            console.log('USER JOINED PARTY', party);
            setParty(party);

            // start polling if admin
            if (party.adminUser?.id === user?.id) {
                pollCurrentlyPlaying = setInterval(() => {
                    getCurrentlyPlaying(party.id);
                }, 10000);

            }
        });


        // NEED TO CHECK IF PARTY EXISTS HERE 
        return () => { socket.emit(SocketEvent.USER_LEFT_PARTY_REQ) };
    }, [])

    const getCurrentlyPlaying = (partyId: string) => {
        console.log('Getting currently playing...');
        axios.get(SPOTIFY_API.CURRENTLY_PLAYING).then((res: AxiosResponse<PlaybackState>) => {

            socket.emit(SocketEvent.PARTY_PLAYBACK_CHANGED_REQ, { playbackState: res.data, partyId: partyId })
        })
    }

    const setCurrentlyPlaying = (playbackState: PlaybackState) => {
        console.log('setting currently playing...', playbackState);
        const args = {
            uris: [playbackState.item.uri]
        }
        axios.put(SPOTIFY_API.PLAY, args)
            .then(() => console.log('set new playbackState'))
    }


    useEffect(() => {
        if (!user || isLoading) {
            console.log('You are not authed');
            return;
        }

        console.log('Logged in as ', user.id);

        socket.emit(SocketEvent.PARTY_JOINED_REQ, { user: user, socketId: socket.id, partyId: partyId })
    }, [user])

    // const onPartyJoinedUnauthed = (numberOfUsers: number) => {
    //     console.log('PARTY JOINED BACK')
    //     setNumberOfUsers(numberOfUsers);
    // }

    return (
        <div>
            {user && user.id ?
                <div>
                    <h1>A duper dope party</h1>
                    <div>Hi, {user.id}!</div>
                    <div>The party admin is: ${party?.adminUser?.id}</div>
                    <div>Users in here: {party?.users?.map(user => user.display_name).join(', ')}</div>
                    {party?.playbackState ?
                        <div>Currently playing: {party?.playbackState?.item.name}</div>
                        :
                        <div>No tracks playing</div>
                    }
                </div>
                :
                <div>This is a party, but you aint authed.</div>}
        </div>);

};

export default PartyPage;
