import React, { createContext, useEffect } from 'react';
import './App.css';
import io from 'socket.io-client'
import API_ENDPOINTS from './endpoints'

import axios, { AxiosResponse } from 'axios';

import { Switch, Route, useHistory } from 'react-router-dom';
import Home from './pages/authenticated/Home';
import { User } from '../../models/User';
import Button from './components/Button';
import { getHashParams } from './helpers/helpers';
// import { AuthContext, Create } from './contexts/AuthContext';


const UserContext = React.createContext<SpotifyUser | undefined>(undefined);
interface AuthHashParams {
  access_token: string;
  token_type: string;
}

interface SpotifyUser {
  id: string;
  display_name: string;
}

function App() {
  const APP_URL = 'http://localhost:8080'
  const LOGIN_URL = APP_URL + '/login';

  const [user, setUser] = React.useState<SpotifyUser>();

  const checkLogIn = (): void => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(savedUser)
    }

    if (!window.location.hash) return;

    let locationArgs: AuthHashParams = getHashParams(window.location.hash);
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + locationArgs.access_token;
    axios.get(API_ENDPOINTS.ME).then((res: AxiosResponse<SpotifyUser>) => {
      console.log(res.data);
      const user = res.data;
      setUser(user)
      localStorage.setItem('user', JSON.stringify(user))
    });
  }

  useEffect(checkLogIn, [])


  // const history = useHistory();

  // const socket = io("http://localhost:8080");


  return (
    <UserContext.Provider value={user}>
      <div className="App">
        <header className="App-header">
        </header>
        <Switch>
          <Route exact path='/'>
            {user && user.id ?
              <div>Hi, {user.id}!</div> :
              <a href={LOGIN_URL}>Login</a>
            }
          </Route>
        </Switch>

      </div>
    </UserContext.Provider>
  );
}

export default App;
