import React, { createContext, useEffect, useState } from 'react';
import './App.css';
import io from 'socket.io-client'
import API_ENDPOINTS from './endpoints'

import axios, { AxiosResponse } from 'axios';

import { Switch, Route, useHistory } from 'react-router-dom';
import Home from './pages/authenticated/Party';
import { User, Party } from '../../models/User';
import Button from './components/Button';
import { getHashParams } from './helpers/helpers';
import { UserProvider, useUser } from './contexts/UserContext';



function App() {
  const APP_URL = 'http://localhost:8080'
  const LOGIN_URL = APP_URL + '/login';

  const { user, logout } = useUser();

  console.log(user);

  const socket = io(APP_URL);
  socket.on('party-created', (party: Party) => {
    console.log(party);
  });

  const createRoom = (): void => {
    socket.emit('party-create', socket.id);
  }

  return (
    <UserProvider>
      <div className="App">
        <header className="App-header">
        </header>
        <Switch>
          <Route exact path='/'>
            {user && user.id ?
              <div>
                <div>Hi, {user.id}!</div>
                <Button name="Logout" onClick={logout}></Button>
                <Button name="Create new Room" onClick={createRoom}></Button>
              </div>
              :
              <a href={LOGIN_URL}>Login</a>
            }
          </Route>
        </Switch>

      </div>
    </UserProvider>
  );
}

export default App;
