import React, { createContext, useEffect, useState } from 'react';
import './App.css';
import io from 'socket.io-client'
import API_ENDPOINTS from './endpoints'

import axios, { AxiosResponse } from 'axios';

import { Switch, Route, useHistory } from 'react-router-dom';
import Home from './pages/authenticated/Home';
import { User, Party } from '../../models/User';
import Button from './components/Button';
import { getHashParams } from './helpers/helpers';
// import { AuthContext, Create } from './contexts/AuthContext';


const UserContext = React.createContext<SpotifyUser | undefined>(undefined);
interface AuthHashParams {
  access_token: string;
  token_type: string;
}

interface SpotifyUser {
  id: string;
  display_name: string;
}

function App() {
  const APP_URL = 'http://localhost:8080'
  const LOGIN_URL = APP_URL + '/login';
  const [otherUsers, setOtherUsers] = useState<SpotifyUser[]>([]);

  // const socket = io(APP_URL);

  // useEffect(() => {
  //   socket.on('party-created', (party: Party) => {
  //     console.log(party);
  //   });
  // }, []);


  const [user, setUser] = React.useState<SpotifyUser>();

  const checkLogIn = (): void => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
      return;
    }

    if (!window.location.hash) return;
    login();
  }

  useEffect(checkLogIn, [])

  const logout = (): void => {
    setUser(undefined);
    localStorage.clear();
  }

  const login = (): void => {
    let locationArgs: AuthHashParams = getHashParams(window.location.hash);
    axios.defaults.headers.common['Authorization'] = 'Bearer ' + locationArgs.access_token;
    axios.get(API_ENDPOINTS.ME).then((res: AxiosResponse<SpotifyUser>) => {
      console.log(res.data);
      const user = res.data;
      setUser(user)
      localStorage.setItem('user', JSON.stringify(user));
      // socket.emit('newconnection', user);
    });
  }

  const createRoom = (): void => {
    // socket.emit('party-create');
  }

  // const history = useHistory();

  // const socket = io("http://localhost:8080");


  return (
    <UserContext.Provider value={user}>
      <div className="App">
        <header className="App-header">
        </header>
        <Switch>
          <Route exact path='/'>
            {user && user.id ?
              <div>
                <div>Hi, {user.id}!</div>
                <Button name="Logout" onClick={logout}></Button>
                <Button name="Create new Room" onClick={createRoom}></Button>
                {otherUsers.map((u: SpotifyUser) => <span>{u.id}</span>)}
              </div>
              :
              <a href={LOGIN_URL}>Login</a>
            }
          </Route>
        </Switch>

      </div>
    </UserContext.Provider>
  );
}

export default App;
