import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import io from 'socket.io-client'
import { EventType } from '../common';
import axios from 'axios';
import Button from '../components/Button';
import { getHashParams } from '../helpers/helpers';
import { useUser } from '../contexts/UserContext';
import socket from '../socket';
import { User } from '../common';


const Party = (): React.ReactElement => {
    const { user } = useUser();
    const [usersInParty, setUsersInParty] = useState<User[]>([]);

    useEffect(() => {
        if (user) {
            socket.emit(Events.USER_JOINED_REQ, user)
        }

        socket.on(Events.USER_JOINED_RES, (newUser: User) => {
            setUsersInParty([...usersInParty, newUser]);
        })
    }, [])

    return (
        <div>
            {user && user.id ?
                <div>
                    <div>Hi, {user.id}!</div>
                </div>
                :
                <div>This is a party, but you aint authed.</div>}
            <div>{usersInParty.map(user => user.display_name)}</div>
        </div>);

};

export default Party;
