import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import io from 'socket.io-client'
import { EventType, Party } from '../common';
import axios from 'axios';
import Button from '../components/Button';
import { getHashParams } from '../helpers/helpers';
import { withRouter, match } from 'react-router';

import { useUser } from '../contexts/UserContext';
import socket from '../socket';
import { User } from '../common';

interface Props {
    match: any;
}

const PartyPage: React.FC<Props> = ({ match }): React.ReactElement => {
    const { user } = useUser();
    const partyId = match.params.id;
    console.log(partyId);

    const [usersInParty, setUsersInParty] = useState<User[]>([]);

    useEffect(() => {
        if (user) {
            socket.emit(EventType.PARTY_JOINED_REQ, { socketId: socket.id, partyId: partyId })
        }

        socket.on(EventType.PARTY_JOINED_RES, (party: Party) => {
            console.lg
        });

        socket.on(EventType.USER_JOINED_RES, (users: User[]) => {
            setUsersInParty(users);
        })
    }, [])

    return (
        <div>
            {user && user.id ?
                <div>
                    <div>Hi, {user.id}!</div>
                </div>
                :
                <div>This is a party, but you aint authed.</div>}
            <div>{usersInParty.map(user => user.display_name)}</div>
        </div>);

};

export default PartyPage;
